import processing.sound.*;
AudioSample sample;

void setup() {
  size(640, 360);
  background(255);

  // Load a soundfile from the data folder of the sketch and get the sample rate
  sample = new AudioSample(this, 100000, 22050);
  println("SampleRate= " + sample.sampleRate() + " Hz");
}      

void draw() {
}
